mapper.select:
SELECT bId, bTitle, bWriter, bPassword, bContent, bDate
FROM fsBoard
WHERE bId = #{bId}

mapper.update:
UPDATE fsBoard
SET bTitle = #{bTitle}, bContent = #{bContent}
WHERE bId = #{bId}

mapper.select:
SELECT bId
FROM fsBoard
WHERE bId = #{bId}
AND bPassword = #{bPassword}

mapper.update:
UPDATE fsBoard
SET bCount = bCount + 1
WHERE bId = #{bId}
