mapper.select:
SELECT bId, bTitle, bWriter, bPassword, bContent, bDate
FROM fsBoard
WHERE bId = #{bId}

mapper.update:
UPDATE fsBoard
SET bCount = bCount + 1
WHERE bId = #{bId}

UPDATE fsBoard
SET bTitle = #{bTitle}, bContent = #{bContent}
WHERE bId = #{bId}

mapper.select:
SELECT bId
FROM fsBoard
WHERE bId = #{bId}
AND bPassword = #{bPassword}

SELECT S.bId, S.bTitle, S.bWriter, S.bContent, S.bDate, S.bCount FROM (SELECT rownum AS rnum, F.bId, F.bTitle, F.bWriter, F.bContent, F.bDate, F.bCount FROM (SELECT bId, bTitle, bWriter, bContent, bDate, bCount FROM fsBoard WHERE
bTitle LIKE '%'||#{keyword}||'%' bContent LIKE '%'||#{keyword}||'%' bTitle||bContent LIKE '%'||#{keyword}||'%' bWriter LIKE '%'||#{keyword}||'%'
ORDER BY bId DESC) F WHERE rownum <= #{lstNum} ) S WHERE S.rnum >= #{firNum}
